import React, { useState } from 'react'

export default function TextForm(props) {
    const [text, setText] = useState("Enter text here");
    const HandleUpClick = () => {
        //console.log("Uppercase button was clicked");
        let newText = text.toUpperCase();
        setText(newText);
    }

    const HandleOnChange = (event) => {
        //console.log("On change");
        setText(event.target.value);
    }
    return (
        <>
            <div className="container">
                <div className="mb-3">
                    <h1>{props.header}</h1>
                    <textarea className="form-control" value={text} onChange={HandleOnChange} id="myText" rows="8" />
                </div>
                <button className="btn btn-primary" onClick={HandleUpClick}>Convert to Uppercase</button>
                <button className="btn btn-primary ml-2" onClick={HandleUpClick}>Convert to Lowercase</button>
            </div>
            <div className="container my-3">
                <h2>Your text summary</h2>
                <p>{text.split(" ").length} wrods and {text.length} characters</p>
                <h3>Preview</h3>
                <p>{text}</p>
            </div>
        </>
    )
}
