import React, { useState } from 'react'

export default function TextForm(props) {
    const aboutReact = `<!DOCTYPE html>
    <html>
    <head>
      <style>
        ul {
          list-style-type: disc;
          margin-left: 20px;
        }
      </style>
    </head>
    <body>
    
    <h1>About React</h1>
    <p>
      React: A JavaScript Library for Building User Interfaces
    </p>
    <ul>
      <li>React is an open-source JavaScript library developed by Facebook for building user interfaces (UIs). It's designed to make it easier to create interactive and dynamic web applications. React follows the component-based architecture, allowing developers to build complex UIs by breaking them down into smaller, reusable components.</li>
    </ul>
    <!-- ... Rest of your HTML content ... -->
    </body>
    </html>`;

    const stripHtmlTags = (htmlString) => {
        const tempDiv = document.createElement("div");
        tempDiv.innerHTML = htmlString;
        return tempDiv.textContent || tempDiv.innerText || "";
    };
    const [text, setText] = useState("");
    const AboutReact = () => {
        //console.log("Uppercase button was clicked");
        let newText = aboutReact;
        setText(stripHtmlTags(aboutReact));
    }
    const HandleUpClick = () => {
        //console.log("Uppercase button was clicked");
        let newText = text.toUpperCase();
        setText(newText);
    }

    const HandleToDelete = () => {
        //console.log("Uppercase button was clicked");
        let newText = "";
        setText(newText);
    }

    const HandleLoClick = () => {
        //console.log("Uppercase button was clicked");
        let newText = text.toLowerCase();
        setText(newText);
    }

    const HandleOnChange = (event) => {
        //console.log("On change");
        setText(event.target.value);
    }
    return (
        <>
            <div className="container">
                <div className="mb-3">
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        <h1 style={{ marginRight: '10px' }}>{props.header}</h1>
                        <button className="btn btn-primary" onClick={AboutReact}>About React</button>
                    </div>

                    <textarea className="form-control" value={text} onChange={HandleOnChange} id="myText" rows="8" />
                </div>
                <button className="btn btn-primary" onClick={HandleUpClick}>Convert to Uppercase </button>
                <button className="btn btn-primary ms-2" onClick={HandleLoClick}>Convert to Lowercase</button>
                <button className="btn btn-danger ms-2" onClick={HandleToDelete}>Clear text</button>
            </div>
            <div className="container my-3">
                <h2>Your text summary</h2>
                <p>
                    {text.length == 0
                        ? "0 words and 0 characters"
                        : `${text.split(" ").length} words and ${text.length} characters`}
                </p>

                <h3>Preview</h3>
                <p>{text}</p>
            </div>
        </>
    )
}
